<div id="experience" class="sect experience">
    <h1>Experience</h1>
    <div class="experience-list">
        {#each experiences as exp}
            <div class="glass-tile" style="--tile-color: {exp.color}">
                <a class="tile-link" href={exp.link} target="_blank" rel="noopener noreferrer">
                    <h2>
                        {exp.title}
                        <span class="arrow" aria-hidden="true">
                               →
                        </span>
                    </h2>
                </a>
                <p>{exp.description}</p>
            </div>
        {/each}
    </div>
</div>

<div id="projects" class="sect proj">
    <h1>Projects</h1>
    <div class="projects-list">
        {#each projects as proj}
            <div class="glass-tile" style="--tile-color: {proj.color}">
                {#if proj.link}
                    <a class="tile-link" href={proj.link} target="_blank" rel="noopener noreferrer">
                        <h2>
                            {proj.title}
                            <span class="arrow" aria-hidden="true">
                               →
                            </span>
                        </h2>
                    </a>
                {:else}
                    <h2 class="tile-link no-link">
                        {proj.title}
                    </h2>
                {/if}
                <p>{proj.description}</p>
            </div>
        {/each}
    </div>
</div>
<Footer />

<style>
    .main {
        height: 100vh;
        width: 100vw;
        overflow-y: scroll;
    }

    #g1045 {
        stroke: white;
        stroke-width: 0.02;
    }

    .hero {
        background: url('/images/bkg.png');
        background-size: cover;
        height: 100vh;
        display: flex;
        flex-direction: column;
        padding-bottom: 0px;
        justify-content: center;
        align-items: center;
        animation: hue-backdrop 5s linear infinite;
    }

    @keyframes hue-backdrop {
        0% {
            filter: hue-rotate(0deg);
        }
        50% {
            filter: hue-rotate(40deg);
        }
        100% {
            filter: hue-rotate(0deg);
        }
    }

    .hero > h1 {
        margin-bottom: 0vh;
        padding-bottom: 0vh;
    }

    .hero > p {
        line-height: 1.5;
        font-family: 'Mozilla Text';
        font-weight: 300;
    }

    .sect {
        display: flex;
        flex-direction: column;
        width: 90vw;
        padding-left: 5vw;
        padding-right: 5vw;
    }

    .sect > h1 {
        font-size: 8vw;
        margin-bottom: 0.5em;
        padding-bottom: 0;
    }

    .sect > p {
        font-size: 1.2em;
        line-height: 1.5;
        padding-top: 0px;
        margin-top: 0;
        text-align: left;
    }

    .dev {
        height: 100vh;
    }

    .experience {
        padding-bottom: 20vh;
        /* height: 120vh; */
        background: linear-gradient(180deg, rgb(11, 11, 26) 0%, var(--gradient-to) 100%);
    }

    .proj {
        background: linear-gradient(180deg, var(--gradient-to) 0%, rgb(20, 0, 100) 100%);
        min-height: 100vh;
        padding-bottom: 15vh;
    }

    .dev .langs {
        width: calc(100vw - 5vw - 5vw);
        /* padding-bottom: 2vh; */
        overflow-x: hidden;
        left: 0;
        margin-left: 0;
        height: 24vh;
        padding-left: -200vw;
        background: linear-gradient(
            90deg,
            rgba(255, 255, 255, 0) 0%,
            rgb(255, 255, 255) 50%,
            rgba(255, 255, 255, 0) 100%
        );
        background-clip: text;
        -webkit-background-clip: text;
    }

    .langs > .langbox {
        height: 24vh;
        padding-bottom: 2vh;
        margin: 0;
        /* margin-left: 50%; */
        padding: 0;
        display: flex;
        flex-direction: row;
        overflow-y: hidden;
        overflow-x: hidden;
    }

    .dev .langbox > p {
        color: transparent;
        font-size: 5em;
        font-family: 'Mozilla Text', 'monospace' !important;
        padding-left: 1vw;
        height: 22vh;
    }

    .const {
        display: flex;
        flex-direction: column;
        width: 100vw;
        align-items: center;
        align-content: center;
        justify-items: center;
        background-color: black;
        padding: 0;
        padding-bottom: 2vh;
    }
    .const > h2 {
        width: auto;
        height: auto;
        padding: 0;
