<div id="experience" class="sect experience">
    <h1>Experience</h1>
    <div class="experience-list">
        {#each experiences as exp}
            <GlassTile
                title={exp.title}
                description={exp.description}
                link={exp.link}
                color={exp.color}
                technologies={exp.technologies}
            />
        {/each}
    </div>
</div>

<div id="projects" class="sect proj">
    <h1>Projects</h1>
    <div class="projects-list">
        {#each projects as proj}
            <GlassTile
                title={proj.title}
                description={proj.description}
                link={proj.link}
                color={proj.color}
                technologies={proj.technologies}
            />
        {/each}
    </div>
</div>
<Footer />

<style>
    .hero {
        background: url('/images/bkg.png');
        background-size: cover;
        height: 100vh;
        display: flex;
        flex-direction: column;
        padding-bottom: 0px;
        justify-content: center;
        align-items: center;
        animation: hue-backdrop 5s linear infinite;
    }

    @keyframes hue-backdrop {
        0% {
            filter: hue-rotate(0deg);
        }
        50% {
            filter: hue-rotate(40deg);
        }
        100% {
            filter: hue-rotate(0deg);
        }
    }

    .hero > h1 {
        margin-bottom: 0vh;
        padding-bottom: 0vh;
    }

    .hero > p {
        line-height: 1.5;
        font-family: 'Mozilla Text';
        font-weight: 300;
        font-size: var(--p-size);
    }

    .sect {
        display: flex;
        flex-direction: column;
        width: 90vw;
        padding-left: 5vw;
        padding-right: 5vw;
        min-height: 70vh;
    }

    .sect > h1 {
        font-size: 8vw;
        margin-bottom: 0.5em;
        padding-bottom: 0;
    }

    .sect > p {
        font-size: var(--p-size);
        line-height: 1.5;
        padding-top: 0px;
        margin-top: 0;
        text-align: left;
    }

    .experience {
        background: linear-gradient(180deg, rgb(11, 11, 26) 0%, var(--gradient-to) 100%);
    }

    .proj {
        background: linear-gradient(180deg, var(--gradient-to) 0%, rgb(20, 0, 100) 100%);
        min-height: 100vh;
        padding-bottom: 15vh;
    }

    .svg_lgo {
        height: 6vh;
        width: 5vw;
    }

    .tooltip {
        font-family: 'Mozilla Text';
        position: absolute;
        bottom: 100%;
        left: 50%;
        transform: translateX(-50%);
        background-color: #333;
        color: white;
        padding: 6px 10px;
        border-radius: 4px;
        font-size: 12px;
        white-space: nowrap;
        opacity: 0;
        visibility: hidden;
        transition:
            opacity 0.3s,
            visibility 0.3s;
        pointer-events: none;
        z-index: 1000;
        margin-bottom: 5px;
    }

    .tooltip::after {
        content: '';
        position: absolute;
        top: 100%;
        left: 50%;
        transform: translateX(-50%);
        border: 5px solid transparent;
        border-top-color: #333;
    }

    .lb {
        display: flex;
        flex-direction: column;
        width: 80vw;
    }
    .lb > .svgs {
        display: flex;
        flex-direction: row;
        flex-wrap: wrap;
    }

    .experience-list,
    .projects-list {
        width: 100%;
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(min(500px, 100%), 1fr));
        gap: 20px;
        justify-content: space-around;
        margin: 0px 0px;
    }

    @media (max-width: 768px) {
        .hero h1 {
            font-size: 2.5rem;
            text-align: center;
        }

        .hero p {
            width: 90% !important;
            text-align: center;
        }

        .sect {
            width: 95vw;
            padding-left: 2.5vw;
            padding-right: 2.5vw;
        }

        .sect > h1 {
            font-size: 2.5rem;
        }
    }

    @media (max-width: 480px) {
        .hero h1 {
            font-size: 2rem;
        }

        .hero p {
            font-size: var(--p-size);
        }

        .sect > h1 {
            font-size: 2rem;
        }
    }

    @media (min-width: 769px) and (max-width: 1024px) {
        .hero h1 {
            font-size: 3rem;
        }

